import React, { useState } from "react";
import { ArrowLeft, CircleX } from "lucide-react";
import ReactSelect from "react-select";
import tableNos from "./tabledetails";
import { CSSTransition } from "react-transition-group"; // Importing CSSTransition

const OrderHistory = ({ orders, setShowOrders, deleteOrder, isVisible }) => {
  const [selectedItem, setSelectedItem] = useState(null);

  const handleChange = (selectedOption) =>
    setSelectedItem(selectedOption?.value);

  const customStyles = {
    control: (styles) => ({ ...styles, borderColor: "#ccc" }),
    option: (styles, { isSelected }) => ({
      ...styles,
      backgroundColor: isSelected ? "#c59d5f" : styles.backgroundColor,
      color: isSelected ? "#fff" : styles.color,
      "&:hover": { backgroundColor: "#f0f0f0" },
    }),
    singleValue: (styles) => ({ ...styles, color: "#333" }),
  };

  const handlePlaceOrder = () => {
    if (!selectedItem) return; // Ensure table number is selected before proceeding
    const orderDetails = orders.map((order) => ({
      name: order.title,
      price: order.price,
      time: new Date().toLocaleTimeString(),
      tableNumber: selectedItem,
      status: "Pending",
    }));
    console.log(orderDetails);
  };

  return (
    <CSSTransition
      in={isVisible} // Controls visibility based on `isVisible` prop
      timeout={300} // Duration of the transition
      classNames="pageSlider" // The class names to be applied for the transition
      unmountOnExit // Ensures component is unmounted when not visible
    >
      <div className="order-history">
        <h2>Order History</h2>
        <button onClick={() => setShowOrders(false)} className="back-btn">
          <ArrowLeft />
          Back to Menu
        </button>
        <ul>
          {orders.map((order, index) => (
            <li
              key={index}
              style={{
                border: "2px solid var(--clr-gold)",
                padding: "20px",
                marginBlock: "10px",
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
              }}
            >
              <div>
                <img
                  src={order.image}
                  alt={order.title}
                  style={{ width: "100px", height: "100px" }}
                />
                <p>
                  {order.title} - ${order.price}
                </p>
              </div>
              <CircleX
                onClick={() => deleteOrder(index)}
                style={{ cursor: "pointer" }}
              />
            </li>
          ))}
        </ul>
        <p>Total: ${orders.reduce((total, order) => total + order.price, 0)}</p>

        <div>
          <div className="card-dd">
            <label htmlFor="table-number">Select Table Number</label>
            <ReactSelect
              id="table-number"
              value={tableNos.find((table) => table.value === selectedItem)}
              onChange={handleChange}
              options={tableNos}
              placeholder="Please select"
              styles={customStyles}
            />
          </div>
          <button onClick={handlePlaceOrder} disabled={!selectedItem}>
            Place Order Now
          </button>
        </div>
      </div>
    </CSSTransition>
  );
};

export default OrderHistory;
