import React, { useState } from "react";
import { ArrowLeft, CircleX } from "lucide-react";
import ReactSelect from "react-select";

const OrderHistory = ({ orders, setShowOrders, deleteOrder }) => {
  const [selectedItem, setSelectedItem] = useState(null);
  const items = [
    { value: 1, label: "Table 1" },
    { value: 2, label: "Table 2" },
    { value: 3, label: "Table 3" },
    { value: 4, label: "Table 4" },
  ];
  return (
    <div>
      <h2>Order History</h2>
      <button onClick={() => setShowOrders(false)} variant="outline">
        <ArrowLeft />
        Back to Menu
      </button>
      <ul>
        {orders.map((order, index) => (
          <li
            key={index}
            style={{
              border: "1px solid #ccc",
              padding: "20px",
              marginBlock: "10px",
            }}
          >
            <img
              src={order.image}
              alt={order.title}
              style={{ width: "100px", height: "100px" }}
            />
            <p>
              {order.title} - ${order.price}
            </p>
            <CircleX
              onClick={() => deleteOrder(index)}
              style={{ cursor: "pointer" }}
            />
          </li>
        ))}
      </ul>
      <p>Total: ${orders.reduce((total, order) => total + order.price, 0)}</p>
      <div style={{}}>
        <div className="card-dd">
          <label htmlFor="table-number">Please select your table number</label>
          <ReactSelect
            id="table-number"
            value={items.find((item) => item.value === selectedItem)}
            onChange={handleChange}
            options={items}
            placeholder="Please select your table number"
            className="list-dd"
          />
        </div>
        <button>Place Order Now</button>
      </div>
    </div>
  );
};

export default OrderHistory;
