import React from "react";
import { ArrowLeft, CircleX } from "lucide-react";

const OrderHistory = ({ orders, setShowOrders }) => {
  return (
    <div>
      <h2>Order History</h2>
      <button onClick={() => setShowOrders(false)} variant="outline">
        <ArrowLeft />
        Back to Menu
      </button>
      <ul>
        {orders.map((order, index) => (
          <li
            key={index}
            style={{
              border: "1px solid #ccc",
              padding: "20px",
              marginBlock: "10px",
            }}
          >
            <img src={order.image} alt={order.title} />
            <p>
              {order.title} - ${order.price}
            </p>
            <button
              onClick={() => {
                const newOrders = orders.filter((o, i) => i !== index);
                localStorage.setItem("orders", JSON.stringify(newOrders));
              }}
            >
              <CircleX />
            </button>
          </li>
        ))}
      </ul>
      <p>Total: ${orders.reduce((total, order) => total + order.price, 0)}</p>
      <button>Place Order Now</button>
    </div>
  );
};

export default OrderHistory;
