import React, { useState } from "react";
import { ArrowLeft, CircleX } from "lucide-react";
import ReactSelect from "react-select";
import tableNos from "./Table";

const OrderHistory = ({ orders, setShowOrders, deleteOrder }) => {
  const [selectedItem, setSelectedItem] = useState(null);
  const handleChange = (selectedOption) =>
    setSelectedItem(selectedOption?.value);

  const customStyles = {
    control: (styles) => ({ ...styles, borderColor: "#ccc" }),
    option: (styles, { isSelected }) => ({
      ...styles,
      backgroundColor: isSelected ? "#c59d5f" : styles.backgroundColor,
      color: isSelected ? "#fff" : styles.color,
      "&:hover": { backgroundColor: "#f0f0f0" },
    }),
    singleValue: (styles) => ({ ...styles, color: "#333" }),
  };

  return (
    <div>
      <h2>Order History</h2>
      <button onClick={() => setShowOrders(false)} variant="outline">
        <ArrowLeft />
        Back to Menu
      </button>
      <ul>
        {orders.map((order, index) => (
          <li
            key={index}
            style={{
              border: "1px solid #ccc",
              padding: "20px",
              marginBlock: "10px",
            }}
          >
            <img
              src={order.image}
              alt={order.title}
              style={{ width: "100px", height: "100px" }}
            />
            <p>
              {order.title} - ${order.price}
            </p>
            <CircleX
              onClick={() => deleteOrder(index)}
              style={{ cursor: "pointer" }}
            />
          </li>
        ))}
      </ul>
      <p>Total: ${orders.reduce((total, order) => total + order.price, 0)}</p>
      <div style={{}}>
        <div className="card-dd">
          <label htmlFor="table-number">Select Table Number</label>
          <ReactSelect
            id="table-number"
            value={tableNos.find((table) => tableNos.value === selectedItem)}
            onChange={handleChange}
            options={items}
            placeholder="Please select"
            styles={customStyles}
          />
        </div>
        <button>Place Order Now</button>
      </div>
    </div>
  );
};

export default OrderHistory;
